import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BlogPost } from '../models/blog-post.model';
import { BlogPostService } from '../services/blog-post.service';
import { Observable, Subscription } from 'rxjs';
import { CategoryService } from '../../category/services/category.service';
import { Category } from '../../category/models/category.model';
import { UpdateBlogPost } from '../models/update-blog-post-request.model';

@Component({
  selector: 'app-edit-blogpost',
  templateUrl: './edit-blogpost.component.html',
  styleUrls: ['./edit-blogpost.component.css']
})
export class EditBlogpostComponent implements OnInit, OnDestroy {
  id: string | null = null;
  model?: BlogPost;
  paramssubscription?: Subscription;  
  categories$?: Observable<Category[]>;
  selectedCategories?: string[];
  updateBlogPostSubscription?: Subscription;
  getBlogPostSubscription?: Subscription;
  deleteBlogPostSubscription?: Subscription;
  showImageSelector?: boolean;
  
  constructor(private route: ActivatedRoute,
    private blogPostService: BlogPostService,
    private categoryService: CategoryService,
    private router: Router) {

  }
  ngOnInit(): void {

    this.categories$ = this.categoryService.getAllCategories();

    this.paramssubscription = this.route.paramMap.subscribe({
      next: (params) => {
        this.id = params.get('id')

        if (this.id) {
          this.getBlogPostSubscription = this.blogPostService.getBlogPostById(this.id).subscribe({
            next: (response) => {
              this.model = response
              this.selectedCategories = response.categories.map(x => x.id);
            }
          });
        }
      }
    })
  }

  onFormSubmit(): void {

    if(this.model && this.id){

      var updateRequestModel: UpdateBlogPost = {
        title : this.model?.title,
        shortDescription : this.model?.shortDescription,
        content : this.model?.content,    
        featuredImageUrl : this.model?.featuredImageUrl,
        urlHandle : this.model?.urlHandle,
        publishedDate : this.model?.publishedDate,
        author : this.model?.author,
        isVisible : this.model?.isVisible,
        categories : this.selectedCategories
      };

      this.updateBlogPostSubscription = this.blogPostService.updateBlogPost(this.id, updateRequestModel).subscribe({
        next: (response) => {
          this.router.navigateByUrl('admin/blogposts')
        }
      });
    }
  }

  OnDelete() : void{
    if(this.id){
      this.deleteBlogPostSubscription =  this.blogPostService.deleteBlogPost(this.id).subscribe({
        next: (response) =>{
          this.router.navigateByUrl('admin/blogposts')
        }
      })
    }

  }

  OpenImageSelector() : void{

  }

  ngOnDestroy(): void {
    this.paramssubscription?.unsubscribe();
    this.updateBlogPostSubscription?.unsubscribe();
    this.getBlogPostSubscription?.unsubscribe();
    this.deleteBlogPostSubscription?.unsubscribe();
  }

}
<div class="container">
    <h1 class="mt-3">Edit BlogPost</h1>

    <ng-container *ngIf="model">
        <form #form="ngForm" (ngSubmit)="onFormSubmit()" name="EditBlogPost">
            <div class="mt-2 mb-5">
                <div class="mt-3">
                    <label for="Title">Title</label>
                    <input type="text" id="Title" class="form-control" name="Title" [(ngModel)]="model.title">
                </div>

                <div class="mt-3">
                    <label for="UrlHandle">UrlHandle</label>
                    <input type="text" id="UrlHandle" class="form-control" name="UrlHandle"
                        [(ngModel)]="model.urlHandle"
                        [ngModelOptions]="{standalone: true}">
                </div>

                <div class="mt-3">
                    <label for="ShortDescription">Short Description</label>
                    <input type="text" id="ShortDescription" class="form-control" name="ShortDescription"
                        [(ngModel)]="model.shortDescription"
                        [ngModelOptions]="{standalone: true}">
                </div>

                <div class="mt-3">
                    <label for="content" class="form-label">Content</label>

                    <div class="d-flex flex-row">
                        <div class="col-6 p-2">
                            <textarea name="content" id="content" class="form-control"
                                style="height: 350px ; max-height: 350px;" [(ngModel)]="model.content"
                                [ngModelOptions]="{standalone: true}"></textarea>
                        </div>
                        <div class="col-6 p-2" style="max-height: 350px; overflow-y: scroll;">
                            <markdown [data]="model.content"></markdown>
                        </div>
                    </div>
                </div>

                <div class="mt-3">
                    <label for="featuredImageUrl" class="form-label">Featured Image Url</label>

                    <ng-container *ngIf="model.featuredImageUrl !== ''">
                        <div>
                            <img [src]="model.featuredImageUrl" height="100" class="my-5">
                        </div>
                    </ng-container>

                    <input type="text" id="featuredImageUrl" name="featuredImageUrl" class="form-control"
                        [(ngModel)]="model.featuredImageUrl"
                        [ngModelOptions]="{standalone: true}">
                </div>

                <div class="mt-3">
                    <label for="publishedDate" class="form-label">Published date</label>
                    <input type="date" 
                    id="publishedDate" 
                    name="publishedDate" 
                    class="form-control"
                    value="{{model.publishedDate}}" 
                    [ngModel]="model.publishedDate | date:'yyy-MM-dd'"
                    (ngModelChange)="model.publishedDate = $event" 
                    [ngModelOptions]="{standalone: true}">
                </div>

                <div class="mt-3">
                    <label for="author" class="form-label">Author</label>
                    <input type="text" id="author" name="author" class="form-control" [(ngModel)]="model.author"
                    [ngModelOptions]="{standalone: true}">
                </div>

                <div class="form-check mt-3">
                    <input class="form-check-input" type="checkbox" id="isVisible" [(ngModel)]="model.isVisible"
                    [ngModelOptions]="{standalone: true}">
                    <label class="form-check-label" for="isVisible">
                        Is Visible?
                    </label>
                </div>

                <div class="mt-3">
                    <label for="categories" class="form-label">Categories</label>
                    <ng-container *ngIf="categories$ | async as categories">
                        <select name="categories" class="form-control" [(ngModel)]="selectedCategories" id="categories"
                            multiple [ngModelOptions]="{standalone: true}">
                            <option *ngFor="let category of categories" [value]="category.id">
                                {{category.name}}
                            </option>
                        </select>
                    </ng-container>
                </div>

                <div class="mt-3 d-flex">
                    <button type="submit" class="btn btn-primary">Save</button>

                    <button type="button" class="btn btn-danger ms-3" (click)="OnDelete()">Delete</button>
                </div>
            </div>
        </form>
    </ng-container>
</div>

